
export class Response {

    constructor() {

    }

    execute(function_name, pgdata, req, res) {
        switch (function_name) {
            case 'Test':
                this.Test(pgdata, req, res);
                break;
                default:
                    res.status(500).json({ error: 'function_name '+function_name+' not found' });
                break;
        }
    }


    Test(pgdata, req, res) {
        res.status(200).json({ ok: 'Personalizado y llamado por pgapi' });
    }


}